/** Datos disponibles en las plantillas preview
{:type:} 	= tipo de archivo (mime-type)
{:size:} 	= tamaño en bytes
{:name:} 	= nombre del archivo
{:ext:} 	= extencion del archivo
{:source:} 	= contenido del archivo en base64
{:image:} 	= tag <img>
**/
(function($) {
	var sPluginName		= "attacher";
  	
	// inicio del plugin
	function Plugin(element) {
		this.element				= element;	// elemento seleccionado
		this.el						= $(element); // jQuery element
		this.name					= sPluginName; // nombre del plugin

		this.aFiles 				= [];
		this.previews				= null;
		this.inputfile				= null;
		this.form					= null;
		
		// valores por defecto
		this.options				= {};
		this.options.form			= true; // boolean, activa/desactiva el control del envio del fomulario contenedor
		this.options.plaintext		= false;

		this.options.target			= null;
		this.options.usepreview		= true;
		this.options.previews		= null;
		this.options.preview		= '<div class="attacher-preview"><span class="attacher-remove">&times;</span><br />{:image:}<br />{:name:}<br />{:size:}<br /></div>';
		
		this.options.name			= "attacher";
		this.options.types			= ["jpg","jpge","png","gif"];
		this.options.maxfiles		= 5; // cantidad maxima de uploads
		this.options.preload		= null; // json con archivos precargados [{"name":"nombre_archivo", "path":"ruta_archivo", "size":"tamaño_bytes"}, {...} ]
		
		// iconos
		this.options.icons			= {
										"default" : "data:image/png;base64,",
										"pdf" : "data:image/png;base64,",
										"doc" : "data:image/png;base64,",
										"docx" : "data:image/png;base64,",
										"xls" : "data:image/png;base64,",
										"xlsx" : "data:image/png;base64,",
										"ppt" : "data:image/png;base64,",
										"pptx" : "data:image/png;base64,",
										"zip" : "data:image/png;base64,",
										"rar" : "data:image/png;base64,",
										"txt" : "data:image/png;base64,"
									};
		
		// eventos
		this.options.onerror		= function() { alert("Error, load fail"); }
		this.options.onattach		= function(preview) { preview.fadeIn("slow"); }
		this.options.beforeremove	= function(preview) { return confirm("Confirm?"); }
		this.options.afterremove	= function(preview) { return preview.fadeOut("fast"); }
		this.options.showpreview	= function() { }
		this.options.autohidetarget	= false; // cuando es true y maxfiles = 1 oculta el target

		this.options.beforesubmit	= function() {}
		this.options.onsubmit		= function() {}
		this.options.onsuccess		= function(response) { location.reload(); }
	}

	// funciones del plugin
	Plugin.prototype = {
		init: function(options) {
			var attacher = this;

			if(typeof $.event.props != "undefined") {
				$.event.props.push("dataTransfer");
			} else {
				$.event.addProp("dataTransfer");
			}

			// se extienden los valores default
			this.options = jQuery.extend({}, this.options, options);

			// target zone
			if(this.options.target==null) {
				var target = $(".attacher-target", this.el);
				if(!target.get(0)) {
					attacher.el.append($("<div>").addClass("attacher-target"));
					target = $(".attacher-target", this.el);
				}
			} else {
				var target = (typeof this.options.target=="string") ? $(this.options.target, attacher.el) : this.options.target;
			}
			target.on("dragover", function(e){
				e.preventDefault();
				return false;
			});
			attacher.form = target.closest("form");
			attacher.form.prop("attacher", attacher);
			var attachers = (attacher.form.prop("attachers")) ? attacher.form.prop("attachers") : [];
			attachers.push(attacher);
			attacher.form.prop("attachers", attachers);
			
			// previews
			if(this.options.usepreview) {
				if(this.options.previews==null) {
					attacher.previews = $(".attacher-previews", this.el);
					if(!attacher.previews.get(0)) {
						attacher.el.append($("<div>").addClass("attacher-previews"));
						attacher.previews = $(".attacher-previews", this.el);
					}
				} else {
					attacher.previews = (typeof this.options.previews=="string") ? $(this.options.previews, attacher.el) : this.options.previews;
				}

				// preview template
				if((attacher.previews.html()).trim()!="") { this.options.preview = attacher.previews.html(); }
				attacher.previews.html("");
			}
			
			// file input
			attacher.inputfile = $("<input>")
							.attr({
								"name": "attacher_"+(String(Math.random()+"").substring(2,12)),
								"type": "file"
							})
							.css("display", "none")
							.on("change", function(e) {
								attacher.mkPreviews(attacher, e.target.files);
							});
			if(this.options.maxfiles>1) { attacher.inputfile.attr("multiple", "multiple"); }

			$("body").append(attacher.inputfile);
			target.click(function(event) { attacher.inputfile.click(); });

			// formulario
			if(attacher.options.form && !attacher.form.prop("submitCached")) {
				attacher.form.prop("submitCached", true);
				attacher.form.attr("enctype", "multipart/form-data");
				attacher.form.submit(function(e){
					e.preventDefault();
					attacher.options.beforesubmit();
					var formData = attacher.prepareForm();
					attacher.options.onsubmit();
					jQuery.ajax({
						url         : $(this).attr("action"),
						type        : "POST",
						data        : formData,
						cache       : false,
						contentType : false,
						processData : false,
						success     : function(data){
							attacher.options.onsuccess(data);
						}
					});
				});
			}

			// previews
			target.on("drop", function(e){
				e.preventDefault();
				attacher.mkPreviews(attacher, e.dataTransfer.files);
			});
			
			attacher.target = target;
			
			// preload
			if(this.options.preload!=null) {
				attacher.mkPreloads(this.options.preload);
			}

			return this;
		},

		prepareForm : function() {
			var formData = new FormData(this.form[0]);
			jQuery.each(this.form.prop("attachers"), function() {
				var attacher = this;
				var sMultiple = (attacher.aFiles.length>1) ? "[]" : "";
				jQuery.each(attacher.aFiles, function() {
					if(!this[1].preload) {
						formData.append(attacher.options.name+sMultiple, this[1].source);
					}
				});
			});

			return formData;
		},

		count: function() {
			return this.aFiles.length;
		},
		
		mkPreviews: function(attacher, files) {
			// var attacher = this;
			if(attacher.previews) { attacher.previews.show(); }
			
			var nCurrentFiles = attacher.count();
			if(files) {
				jQuery.each(files, function(index, file) {
					// limite de archivos
					if(attacher.options.maxfiles==nCurrentFiles) {
						alert("Attachs limit exceeded");
						return false;
					}
					nCurrentFiles++;
					
					// tipo de archivo
					var extension = file.name.split(".");
					extension = extension[extension.length-1];
					if($.inArray(extension, attacher.options.types)<0) {
						alert("Invalid type: "+files[index].type);
						return false;
					}

					var reader = new FileReader();
					reader.onload = (function(file) {
						return function(e) {
							if(reader.readyState==2) {
								var idx = "attacher-"+(String(Math.random()+"").substring(2,12));
								attacher.aFiles.push([idx,{
									source: file,
									name: file.name,
									size: file.size,
									type: file.type,
									ext: extension,
									content: this.result,
									preload: false
								}]);
							} else {
								attacher.options.onerror();
							}
						};
					})(files[index]);
					
					if(attacher.options.plaintext) {
						reader.readAsText(file);
					} else {
						reader.readAsDataURL(file);
					}
				});
			}

			var loaded = setInterval(function() {
				if(nCurrentFiles==attacher.count()) {
					clearInterval(loaded);
					attacher.insertPreviews(attacher);
				}
			}, 500);
		},
		
		mkPreloads: function(preloads) {
			var idx = null;
			var attacher = this;
			var x = 0;
			jQuery.each(preloads, function() {
				if(attacher.options.maxfiles==attacher.count()) { return true; }

				idx = "attacher-"+(String(Math.random()+"").substring(2,12));
				
				var file = document.createElement("a");
				file.href = this.path;

				if(file.href.substr(file.href.length - 1)!="/") {
					var extention = file.pathname.substring(file.pathname.lastIndexOf(".")+1);
					var isimg = (attacher.isImage(extention));
					var filename = this.name || file.pathname.substring(file.pathname.lastIndexOf("/")+1);
					var link = $("<div>").html($("<a>").attr({"href":file.href, "target":"_blank"}).html(filename));
					attacher.aFiles.push([idx,{
						realname: file.pathname.substring(file.pathname.lastIndexOf("/")+1),
						filename: filename,
						name: link.html(),
						size: this.size || 0,
						type: (isimg) ? "image" : "attach",
						ext: extention,
						content: file.href,
						preload: true
					}]);
				}
			});

			if(idx) { attacher.mkPreviews(attacher); }
		},
		
		insertPreviews: function(attacher) {
			if(attacher.previews) { attacher.previews.html(""); }

			jQuery.each(attacher.aFiles, function() {
				var sPreview = attacher.options.preview;

				var idx = this[0];
				var file = this[1];
				
				if(file.type.indexOf("image")>=0) {
					var content = file.content;
				} else {
					var content = (attacher.options.icons[file.ext]) ? attacher.options.icons[file.ext] : attacher.options.icons["default"];
				}

				if(attacher.options.usepreview) {
					sPreview = sPreview.replace(/\{:type:\}/g, file.type);
					sPreview = sPreview.replace(/\{:size:\}/g, file.size);
					sPreview = sPreview.replace(/\{:name:\}/g, file.name);
					sPreview = sPreview.replace(/\{:ext:\}/g, file.ext);
					sPreview = sPreview.replace(/\{:source:\}/g, content);
					sPreview = sPreview.replace(/\{:image:\}/g, '<img src="'+content+'" />');
					var preview = $(sPreview);
					
					$(".attacher-remove", preview).on("click", function(){
						if(attacher.options.beforeremove($("#"+idx))) {
							attacher.remove(idx);
						}
					});

					attacher.previews.append(preview);
					attacher.options.onattach(preview);
				} else {
					attacher.options.onattach(file);
				}
				
				if(attacher.options.autohidetarget && attacher.options.maxfiles==attacher.count()) {
					attacher.target.hide();
				}
			});
		},

		remove: function(idx) {
			var attacher = this;
			var aNewFiles = [];

			jQuery.each(attacher.aFiles, function() {
				if(this[0]!=idx) {
					aNewFiles.push(this);
				} else {
					$("<input>", {type:"hidden", name:attacher.options.name+"_remove[]", value:this[1].realname}).appendTo(attacher.form);
				}
			});

			attacher.aFiles = aNewFiles;
			
			jQuery.when(attacher.options.afterremove($("#"+idx))).then(
				function() { attacher.insertPreviews(attacher); }
			)
			
			if(attacher.options.autohidetarget && attacher.options.maxfiles==1) {
				attacher.target.show();
			}
		},
		
		removeAll: function() {
			this.aFiles = [];
			this.insertPreviews();
		},
		
		isImage: function(extention) {
			var formats = ["jpg","jpeg","gif","png","apng","svg","bmp","ico"];
			return (jQuery.inArray(extention.toLowerCase(), formats) >= 0);
		}
	};
	
	// se incorpora el plugin al objeto jQuery -----------------------------------------------------
	jQuery.fn[sPluginName] = function(options, parameters) {
		this.each(function() {
			var firstTime = false;
			
			// crea el plugin
			if(!jQuery.data(this, sPluginName)) {
				jQuery.data(this, sPluginName, new Plugin(this));
				var firstTime = true;
			}

			// ejecuta el llamado a un metodo
			if(typeof(jQuery.data(this, sPluginName)[options])==="function") {
				if(firstTime) { jQuery.data(this, sPluginName)["init"](); }
				jQuery.data(this, sPluginName)[options](parameters);
			} else {
				if(firstTime) { jQuery.data(this, sPluginName)["init"](options); }
			}

			// destructor
			if(options==="destroy") {
				delete jQuery.data(this, sPluginName);
			}
		});

		return this;
	};
})(jQuery);